import React from "react";
import { Button, NavDropdown, OverlayTrigger, Tooltip } from "react-bootstrap";
import {
  Minecart,
  MinecartLoaded,
  ArrowRight,
  Eraser,
} from "react-bootstrap-icons";

export default class Canvas extends React.Component {
  render() {
    return (
      <div className="items">
        {this.props.items.length > 0 ? (
          <NavDropdown
            id="cart-dropdown"
            title={<MinecartLoaded />}
            autoClose="outside"
            align={{ lg: "end" }}
          >
            {this.props.items.map((item, index) => (
              <div>
                <NavDropdown.Item className="item">
                  <div style={{ display: "flex" }}>
                    <span>{item.i}</span>
                    <EraserFill
                      onClick={this.props.onTakeItem.bind(undefined, item)}
                    />
                  </div>
                </NavDropdown.Item>
                {index < this.props.items.length - 1 ? (
                  <NavDropdown.Divider />
                ) : null}
              </div>
            ))}
            <NavDropdown.Divider />
            <NavDropdown.Item className="item" style={{ textAlign: "center" }}>
              Checkout <ArrowRight />
            </NavDropdown.Item>
          </NavDropdown>
        ) : (
          <OverlayTrigger
            placement="left"
            overlay={
              <Tooltip>
                Cart is empty. Go add some items to <strong>spark</strong> your
                next big idea! ðŸ’¡
              </Tooltip>
            }
          >
            <NavDropdown
              id="cart-dropdown-disabled"
              title={<Minecart />}
              autoClose={true}
              align={{ lg: "end" }}
              disabled={true}
            >
              <div style={{ paddingLeft: "3%" }}>Go add some items!</div>
            </NavDropdown>
          </OverlayTrigger>
        )}
      </div>
    );
  }
}
