import React from "react";
import { NavDropdown } from "react-bootstrap";
import { Minecart, MinecartLoaded } from "react-bootstrap-icons";

export default class Canvas extends React.Component {
  state = {
    size: 0,
  };
  render() {
    return (
      <div className="items">
        {size > 0 ? (
          <NavDropdown
            id="cart-dropdown"
            title={<MinecartLoaded />}
            autoClose="outside"
          >
            {this.props.items.map((item, index) => (
              <div>
                <NavDropdown.Item
                  className="item"
                  onClick={this.props.onTakeItem.bind(undefined, item)}
                >
                  {item.i}
                </NavDropdown.Item>
                {index < size - 1 ? <NavDropdown.Divider /> : null}
              </div>
            ))}
          </NavDropdown>
        ) : (
          <NavDropdown
            id="cart-dropdown-disabled"
            title={<Minecart />}
            autoClose={true}
            disabled={true}
          ></NavDropdown>
        )}
      </div>
    );
  }
}
