// Block component used in Canvas' grid
import React from "react";
import { Card } from "react-bootstrap";

export default class Block extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isSelected: false,
    };
  }

  render() {
    const { block, onClick } = this.props;

    const { isSelected } = this.state;

    const style = {
      backgroundColor: block.color,
    };

    return (
      <Card
        style={style}
        onClick={() => {
          onClick(block.id);
        }}
      >
        <Card.Body>
          <Card.Title>{block.id}</Card.Title>
        </Card.Body>
      </Card>
    );
  }
}
