// Nav Bar Component
import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import {
  Form,
  Navbar,
  Container,
  Nav,
  FormControl,
  Button,
} from "react-bootstrap";
import { Search } from "react-bootstrap-icons";
import Canvas from "./Canvas";

export default class NavBar extends React.Component {
  constructor(props) {
    super(props);

    this.state = { scrollBackground: "nav-bg" };
    this.handleScroll = this.handleScroll.bind(this);
  }

  handleScroll() {
    this.setState({
      scrollBackground: !this.state.scrollBackground,
    });
  }

  render() {
    return (
      <Navbar
        inverse
        toggleable
        className={this.state.scrollBackground}
        onScroll={this.handleScroll}
      >
        <Container fluid>
          <Navbar.Brand href="#">Ignite</Navbar.Brand>
          <Navbar.Toggle aria-controls="navbarScroll" />
          <Navbar.Collapse id="navbarScroll">
            <Nav className="m-auto" style={{ maxHeight: "100px" }} navbarScroll>
              <Form className="d-flex">
                <FormControl
                  type="search"
                  placeholder="Search for ideas..."
                  className="me-2"
                  aria-label="Search"
                />
                <Button variant="primary" size="sm">
                  <Search />
                </Button>
              </Form>
              <Button
                variant="outline-primary"
                className="shadow-none"
                onClick={this.props.onNewLayout}
              >
                Shuffle
              </Button>
            </Nav>
            <Canvas
              items={this.props.items}
              onTakeItem={this.props.onTakeItem}
            />
          </Navbar.Collapse>
        </Container>
      </Navbar>
    );
  }
}
