import React from "react";
import { NavDropdown } from "react-bootstrap";
import { Minecart, MinecartLoaded } from "react-bootstrap-icons";

export default class Canvas extends React.Component {
  render() {
    return (
      <div className="items">
        {this.props.items.length > 0 ? (
          <NavDropdown
            id="cart-dropdown"
            title={<MinecartLoaded />}
            autoClose="outside"
          >
            <NavDropdown.Menu>
              {this.props.items.map((item) => (
                <div>
                  <NavDropdown.Item
                    className="item"
                    onClick={this.props.onTakeItem.bind(undefined, item)}
                  >
                    {item.i}
                  </NavDropdown.Item>
                  <NavDropdown.Divider />
                </div>
              ))}
            </NavDropdown.Menu>
          </NavDropdown>
        ) : (
          <NavDropdown
            id="cart-dropdown-disabled"
            title={<Minecart />}
            autoClose={true}
            disabled={true}
          ></NavDropdown>
        )}
      </div>
    );
  }
}
