import React from "react";
import { NavDropdown } from "react-bootstrap";
import { Minecart, MinecartLoaded, ArrowRight } from "react-bootstrap-icons";

export default class Canvas extends React.Component {
  render() {
    return (
      <div className="items">
        {this.props.items.length > 0 ? (
          <NavDropdown
            id="cart-dropdown"
            title={<MinecartLoaded />}
            autoClose="outside"
            align={{ lg: "end" }}
          >
            {this.props.items.map((item, index) => (
              <div>
                <NavDropdown.Item
                  className="item"
                  onClick={this.props.onTakeItem.bind(undefined, item)}
                >
                  {item.i}
                </NavDropdown.Item>
                {index < this.props.items.length - 1 ? (
                  <NavDropdown.Divider />
                ) : null}
              </div>
            ))}
            <NavDropdown.Divider />
            <NavDropdown.Item className="item" style={{ textAlign: "center" }}>
              Checkout <ArrowRight />
            </NavDropdown.Item>
          </NavDropdown>
        ) : (
          <OverlayTrigger
            key={placement}
            placement={placement}
            overlay={
              <Tooltip id={`tooltip-${placement}`}>
                Tooltip on <strong>{placement}</strong>.
              </Tooltip>
            }
          >
            <NavDropdown
              id="cart-dropdown-disabled"
              title={<Minecart />}
              autoClose={true}
              align={{ lg: "end" }}
              disabled={true}
            >
              <div style={{ paddingLeft: "3%" }}>Go add some items!</div>
            </NavDropdown>
          </OverlayTrigger>
        )}
      </div>
    );
  }
}
